function setIconCallback(e){e&&e.runState?chrome.browserAction.setIcon({path:{16:"icons/gusmd16-active.png"}}):chrome.browserAction.setIcon({path:{16:"icons/gusmd16-idle.png"}})}var chrome=window.chrome;chrome.browserAction.onClicked.addListener(function(){chrome.tabs.query({active:!0,currentWindow:!0},function(e){chrome.tabs.sendMessage(e[0].id,{changeRun:!0},function(e){setIconCallback(e)})})}),chrome.tabs.onActivated.addListener(function(e){chrome.tabs.get(e.tabId,function(e){chrome.tabs.query({active:!0,currentWindow:!0},function(e){chrome.tabs.sendMessage(e[0].id,{getCurrentRunState:!0},function(e){setIconCallback(e)})})})}),chrome.tabs.onUpdated.addListener(function(e,n,t){chrome.tabs.query({active:!0,currentWindow:!0},function(e){chrome.tabs.sendMessage(e[0].id,{getCurrentRunState:!0},function(e){setIconCallback(e)})})});
